import * as React from 'react';
import { browserHistory, RouteComponentProps } from 'react-router';
import { Menu, Icon, Avatar, Popover } from 'antd';
import './Frame.scss';
const MenuItemGroup = Menu.ItemGroup;

export default class Frame extends React.Component<RouteComponentProps<any, any>> {
  handleRouting = item => {
    browserHistory.push(item.key);
  }

  renderAvatar = () => {
    return <div>
      <Icon type="logout" />注销
    </div>
  }

  render() {
    const {location:{pathname}} = this.props;
    return <div className="frame">
      <Menu onClick={this.handleRouting} className="left-menu" selectedKeys={[pathname]}>
        <MenuItemGroup title={<><div className="frame-menu-icon"/>数据管理平台</>}>
          <Menu.Item key="/metaCenter"><Icon type="database" />元数据中心</Menu.Item>
          <Menu.Item key="/reports"><Icon type="area-chart" />报表管理</Menu.Item>
          <Menu.Item key="/logs"><Icon type="profile" />日志管理</Menu.Item>
        </MenuItemGroup>
      </Menu>
      <div className="content">
        <div className="header">
          <Popover placement="bottomRight" content={this.renderAvatar()}>
            <Avatar icon="user" style={{marginLeft: 'auto', marginRight: 20}}/>
          </Popover>
        </div>
        {this.props.children}
      </div>
    </div>
  }
}
